DECLARE
 texto CLOB := '{"consulta":"select distinct tipo.codigo || acao.codigo               as codigo,\r\n                acao.descricao                           as descricao,\r\n                tipo.descricao                           as tipo,\r\n                prog.codigo || '' - '' || prog.denominacao as programa,\r\n                acao.id\r\nfrom acaoppa acao\r\n         inner join tipoacaoppa tipo on acao.tipoacaoppa_id = tipo.id\r\n         inner join subacaoppa sub on acao.id = sub.acaoppa_id\r\n         inner join provisaoppadespesa prov on sub.id = prov.subacaoppa_id\r\n         inner join programappa prog on acao.programa_id = prog.id\r\n         inner join exercicio e on acao.exercicio_id = e.id\r\nwhere e.ano = $ANO\r\n$WHERE\r\norder by codigo","count":"select count(distinct acao.id)\r\nfrom acaoppa acao\r\n         inner join tipoacaoppa tipo on acao.tipoacaoppa_id = tipo.id\r\n         inner join subacaoppa sub on acao.id = sub.acaoppa_id\r\n         inner join provisaoppadespesa prov on sub.id = prov.subacaoppa_id\r\n         inner join programappa prog on acao.programa_id = prog.id\r\n         inner join exercicio e on acao.exercicio_id = e.id\r\nwhere e.ano = $ANO\r\n$WHERE","totalRegistros":456,"fields":[{"posicao":1,"descricao":"CÓDIGO","columnName":"CODIGO","columnValue":"CODIGO","tipoAlinhamento":"ESQUERDA","totalizar":false,"link":{"chaveDestino":"acao-ver","columnOrigem":"ID","columnDestino":"acao.id"},"tipo":"STRING","tipoEnum":null,"valorPadrao":null,"escondido":false},{"posicao":2,"descricao":"DESCRIÇÃO","columnName":"DESCRICAO","columnValue":"DESCRICAO","tipoAlinhamento":"ESQUERDA","totalizar":false,"link":null,"tipo":"STRING","tipoEnum":null,"valorPadrao":null,"escondido":false},{"posicao":3,"descricao":"TIPO","columnName":"TIPO","columnValue":"TIPO","tipoAlinhamento":"ESQUERDA","totalizar":false,"link":null,"tipo":"STRING","tipoEnum":null,"valorPadrao":null,"escondido":false},{"posicao":4,"descricao":"PROGRAMA","columnName":"PROGRAMA","columnValue":"PROGRAMA","tipoAlinhamento":"ESQUERDA","totalizar":false,"link":null,"tipo":"STRING","tipoEnum":null,"valorPadrao":null,"escondido":false},{"posicao":5,"descricao":"ID","columnName":"ID","columnValue":"ID","tipoAlinhamento":"ESQUERDA","totalizar":false,"link":null,"tipo":"STRING","tipoEnum":null,"valorPadrao":null,"escondido":true}],"pesquisaveis":[],"tabs":[]}';
BEGIN
update PAGINAPREFEITURAPORTAL set CONTEUDO = texto where NOME = 'Ações' and CHAVE = 'acao';
END;
