DECLARE
 texto CLOB := '{"consulta":"select distinct tipo.codigo || acao.codigo               as codigo,\r\n                acao.descricao                           as descricao,\r\n                tipo.descricao                           as tipo,\r\n                prog.codigo || '' - '' || prog.denominacao as programa,\r\n                funcao.codigo || '' - '' || funcao.descricao as funcao,\r\n                subfuncao.codigo || '' - '' || subfuncao.descricao as subfuncao,\r\n                unidade.descricao as unidade,\r\n                acao.totalfisico as totalFisico,\r\n                acao.totalfinanceiro as totalFinanceiro\r\nfrom acaoppa acao\r\n         inner join tipoacaoppa tipo on acao.tipoacaoppa_id = tipo.id\r\n         inner join subacaoppa sub on acao.id = sub.acaoppa_id\r\n         inner join provisaoppadespesa prov on sub.id = prov.subacaoppa_id\r\n         inner join programappa prog on acao.programa_id = prog.id\r\n        inner join funcao on acao.funcao_id = funcao.id\r\n        inner join subfuncao on acao.subfuncao_id = subfuncao.id\r\n        inner join unidadeorganizacional unidade on acao.responsavel_id = unidade.id\r\n         inner join exercicio e on acao.exercicio_id = e.id\r\n         $WHERE","count":"select  count(acao.id)\r\nfrom acaoppa acao\r\n         inner join tipoacaoppa tipo on acao.tipoacaoppa_id = tipo.id\r\n         inner join subacaoppa sub on acao.id = sub.acaoppa_id\r\n         inner join provisaoppadespesa prov on sub.id = prov.subacaoppa_id\r\n         inner join programappa prog on acao.programa_id = prog.id\r\n        inner join funcao on acao.funcao_id = funcao.id\r\n        inner join subfuncao on acao.subfuncao_id = subfuncao.id\r\n        inner join unidadeorganizacional unidade on acao.responsavel_id = unidade.id\r\n         inner join exercicio e on acao.exercicio_id = e.id","totalRegistros":16971,"fields":[{"posicao":1,"descricao":"CÓDIGO","columnName":"CODIGO","columnValue":"CODIGO","tipoAlinhamento":"ESQUERDA","totalizar":false,"link":null,"tipo":"STRING","tipoEnum":null,"valorPadrao":null,"escondido":false},{"posicao":2,"descricao":"DESCRIÇÃO","columnName":"DESCRICAO","columnValue":"DESCRICAO","tipoAlinhamento":"ESQUERDA","totalizar":false,"link":null,"tipo":"STRING","tipoEnum":null,"valorPadrao":null,"escondido":false},{"posicao":3,"descricao":"TIPO","columnName":"TIPO","columnValue":"TIPO","tipoAlinhamento":"ESQUERDA","totalizar":false,"link":null,"tipo":"STRING","tipoEnum":null,"valorPadrao":null,"escondido":false},{"posicao":4,"descricao":"PROGRAMA","columnName":"PROGRAMA","columnValue":"PROGRAMA","tipoAlinhamento":"ESQUERDA","totalizar":false,"link":null,"tipo":"STRING","tipoEnum":null,"valorPadrao":null,"escondido":false},{"posicao":5,"descricao":"FUNÇÃO","columnName":"FUNCAO","columnValue":"FUNCAO","tipoAlinhamento":"ESQUERDA","totalizar":false,"link":null,"tipo":"STRING","tipoEnum":null,"valorPadrao":null,"escondido":false},{"posicao":6,"descricao":"SUB-FUNÇÃO","columnName":"SUBFUNCAO","columnValue":"SUBFUNCAO","tipoAlinhamento":"ESQUERDA","totalizar":false,"link":null,"tipo":"STRING","tipoEnum":null,"valorPadrao":null,"escondido":false},{"posicao":7,"descricao":"UNIDADE","columnName":"UNIDADE","columnValue":"UNIDADE","tipoAlinhamento":"ESQUERDA","totalizar":false,"link":null,"tipo":"STRING","tipoEnum":null,"valorPadrao":null,"escondido":false},{"posicao":8,"descricao":"TOTAL FÍSICO","columnName":"TOTALFISICO","columnValue":"TOTALFISICO","tipoAlinhamento":"ESQUERDA","totalizar":true,"link":null,"tipo":"MONETARIO","tipoEnum":null,"valorPadrao":null,"escondido":false},{"posicao":9,"descricao":"TOTAL FINANCEIRO","columnName":"TOTALFINANCEIRO","columnValue":"TOTALFINANCEIRO","tipoAlinhamento":"ESQUERDA","totalizar":true,"link":null,"tipo":"MONETARIO","tipoEnum":null,"valorPadrao":null,"escondido":false}],"pesquisaveis":[],"tabs":[]}';
