insert into codigoco values (hibernate_sequence.nextval, '3120', 'Identificação das Transferências da União decorrentes de emendas parlamentares de bancada', 'Transferências decorrentes de emendas parlamentares de bancada, na forma prevista no parágrafo 11 do art. 166, da CF/88, acrescido pela Emenda Constitucional nº 100/2019. Esse marcador deverá ser associado às fontes de recursos referentes às transferências decorrentes de emendas obrigatórias, na fase de arrecadação da receita, no controle dos ativos e passivos e na fase de execução das despesas custeadas com esses recursos.');
insert into codigoco values (hibernate_sequence.nextval, '3210', 'Identificação das Transferências dos Estados decorrentes de emendas parlamentares individuais', 'Transferências decorrentes de emendas parlamentares individuais, na forma prevista nas Constituições Estaduais de forma similar ao previsto no parágrafo 9º do art. 166, da CF/88. Esse marcador, de utilização pelos municípios, será associado às fontes de recursos referentes às transferências decorrentes de emendas obrigatórias dos estados, devendo ser utilizado na fase da arrecadação da receita, no controle dos ativos e passivos e na fase de execução das despesas custeadas com esses recursos.');
insert into codigoco values (hibernate_sequence.nextval, '3220', 'Identificação das Transferências dos Estados decorrentes de emendas parlamentares de bancada', 'Transferências decorrentes de emendas parlamentares de bancada, na forma prevista nas Constituições Estaduais, de forma similar ao previsto no parágrafo 11 do art. 166, da CF/88. Esse marcador, de utilização pelos municípios, deverá ser associado às fontes de recursos referentes às transferências decorrentes de emendas obrigatórias dos estados, devendo ser utilizado na fase de arrecadação da receita, no controle dos ativos e passivos e na fase de execução das despesas custeadas com esses recursos.');
