texto CLOB := '{"consulta":"select case when mes is not null and (mes + 1) > 9 then to_char(mes + 1) else ''0'' || to_char(mes + 1) end || ''/'' || ano as mesAno,\r\n       matricula,\r\n       servidor,\r\n       cpf,\r\n       cargo,\r\n       unidade,\r\n       tipoRegime,\r\n       jornadaTrabalho,\r\n       ato,\r\n       dataadmissao,\r\n       dataPublicacaoAto,\r\n       recurso,\r\n       case\r\n           when pensionista is not null then ''PENSIONISTA''\r\n           when aposentado is not null then ''APOSENTADO''\r\n           when estagiario is not null then ''ESTAGIÁRIO''\r\n           else vinculo end as vinculo,\r\n       tipodefolha,\r\n       coalesce(sum(vencimentobase), 0)                                            as vencimentobase,\r\n       coalesce(sum(valorbruto), 0) - coalesce(sum(vencimentobase), 0)             as outrasverbas,\r\n       coalesce(sum(valorbruto), 0)                                                as salariobruto,\r\n       coalesce(sum(valordesconto), 0)                                             as descontos,\r\n       coalesce(sum(coalesce(valorbruto, 0)) - sum(coalesce(valordesconto, 0)), 0) as salarioliquido,\r\n       coalesce(sum(valorir), 0)                                                   as impostorenda,\r\n       coalesce(sum(valorprevidencia), 0)                                          as previdencia,\r\n       coalesce(sum(valorabateteto), 0)                                            as abateteto,\r\n       coalesce(sum(valordesconto), 0) - coalesce(sum(valorir), 0) - coalesce(sum(valorprevidencia), 0) - coalesce(sum(valorabateteto), 0) as outrosdescontos,\r\n       idato,\r\n       idpessoa\r\nfrom (select mat.matricula || ''/'' || v.numero                                                                     as matricula,\r\n             pf.nome                                                                                              as servidor,\r\n             formatacpfcnpj(''***'' || substr(pf.cpf, 4, length(cpf) - 5) || ''**'')                                  as cpf,\r\n             tr.descricao                                                                                         as tipoRegime,\r\n             ''Horas Semanais: '' || jdt.horasSemanal || '', Horas Mensais: '' || jdt.horasMensal                     as jornadaTrabalho,\r\n             ''N° '' || ato.numero || '' - '' || ato.nome                                                             as ato,\r\n             ato.datapublicacao                                                                                   as dataPublicacaoAto,\r\n             folha.mes                                                                                            as mes,\r\n             folha.ano                                                                                            as ano,\r\n             folha.tipofolhadepagamento                                                                           as tipodefolha,\r\n             to_char(cfp.dataadmissao, ''dd/MM/yyyy'')                                                              as dataadmissao,\r\n             cargo.codigodocargo || '' - '' || cargo.descricao                                                      as cargo,\r\n             mcfp.descricao                                                                                       as vinculo,\r\n             ho.descricao                                                                                         as unidade,\r\n             recfp.descricao                                                                                      as recurso,\r\n             pens.id                                                                                              as pensionista,\r\n             aposent.id                                                                                           as aposentado,\r\n             estag.id                                                                                             as estagiario,\r\n             (select sum(item.valor)\r\n              from folhadepagamento folha\r\n                       inner join fichafinanceirafp ficha on ficha.folhadepagamento_id = folha.id\r\n                       inner join itemfichafinanceirafp item on item.fichafinanceirafp_id = ficha.id\r\n                       inner join itembasefp itembase on itembase.eventofp_id = item.eventofp_id\r\n                       inner join basefp b on b.id = itembase.basefp_id\r\n              where ficha.vinculofp_id = v.id\r\n                and itemficha.id = item.id\r\n                and item.tipoeventofp = ''VANTAGEM''\r\n                and b.codigo = (select conf.vencimentobaseportal\r\n                                from configuracaorh conf\r\n                                where to_date(''01/01/'' || folha.ano, ''dd/MM/yyyy'')\r\n                                          between conf.iniciovigencia\r\n                                          and coalesce(conf.finalvigencia, to_date(''01/01/'' || folha.ano, ''dd/MM/yyyy'')))) as vencimentobase,\r\n             (select sum(item.valor)\r\n              from folhadepagamento folha\r\n                       inner join fichafinanceirafp ficha on ficha.folhadepagamento_id = folha.id\r\n                       inner join itemfichafinanceirafp item on item.fichafinanceirafp_id = ficha.id\r\n                       inner join eventofp e on e.id = item.eventofp_id\r\n              where ficha.vinculofp_id = v.id\r\n                and item.id = itemficha.id\r\n                and item.tipoeventofp = ''DESCONTO''\r\n                and e.identificacaoeventofp in (''RPPS'', ''INSS''))                                                  as valorprevidencia,\r\n             (select sum(item.valor)\r\n              from folhadepagamento folha\r\n                       inner join fichafinanceirafp ficha on ficha.folhadepagamento_id = folha.id\r\n                       inner join itemfichafinanceirafp item on item.fichafinanceirafp_id = ficha.id\r\n                       inner join eventofp e on e.id = item.eventofp_id\r\n              where ficha.vinculofp_id = v.id\r\n                and item.id = itemficha.id\r\n                and item.tipoeventofp = ''DESCONTO''\r\n                and e.codigo in (635))                                                                            as valorabateteto,\r\n             (select sum(item.valor)\r\n              from folhadepagamento folha\r\n                       inner join fichafinanceirafp ficha on ficha.folhadepagamento_id = folha.id\r\n                       inner join itemfichafinanceirafp item on item.fichafinanceirafp_id = ficha.id\r\n                       inner join eventofp e on e.id = item.eventofp_id\r\n              where ficha.vinculofp_id = v.id\r\n                and item.id = itemficha.id\r\n                and item.tipoeventofp = ''DESCONTO''\r\n                and e.identificacaoeventofp = ''IMPOSTO_RENDA'')                                                    as valorir,\r\n\r\n             (select sum(item.valor)\r\n              from folhadepagamento folha\r\n                       inner join fichafinanceirafp ficha on ficha.folhadepagamento_id = folha.id\r\n                       inner join itemfichafinanceirafp item on item.fichafinanceirafp_id = ficha.id\r\n              where ficha.vinculofp_id = v.id\r\n                and item.id = itemficha.id\r\n                and item.tipoeventofp = ''VANTAGEM'')                                                               as valorbruto,\r\n             (select sum(item.valor)\r\n              from folhadepagamento folha\r\n                       inner join fichafinanceirafp ficha on ficha.folhadepagamento_id = folha.id\r\n                       inner join itemfichafinanceirafp item on item.fichafinanceirafp_id = ficha.id\r\n              where ficha.vinculofp_id = v.id\r\n                and item.id = itemficha.id\r\n                and item.tipoeventofp = ''DESCONTO'')                                                               as valordesconto,\r\n                ato.id as idato,\r\n                pf.id as idpessoa\r\n      from vinculofp v\r\n               inner join matriculafp mat on mat.id = v.matriculafp_id\r\n               inner join pessoafisica pf on pf.id = mat.pessoa_id\r\n               left join pessoajuridica pj on mat.pessoa_id = pj.id\r\n               inner join fichafinanceirafp ff on ff.vinculofp_id = v.id\r\n               inner join folhadepagamento folha on folha.id = ff.folhadepagamento_id\r\n               inner join itemfichafinanceirafp itemficha on itemficha.fichafinanceirafp_id = ff.id\r\n               inner join unidadeorganizacional uo on uo.id = ff.unidadeorganizacional_id\r\n               inner join recursofp recfp on recfp.id = ff.recursofp_id\r\n               inner join vwhierarquiaadministrativa ho on ho.subordinada_id = uo.id\r\n               left join aposentadoria aposent on aposent.id = v.id\r\n               left join pensionista pens on pens.id = v.id\r\n               left join entidade ent on ent.id = uo.entidade_id\r\n               left join pensao pen on pen.id = pens.pensao_id\r\n               left join estagiario estag on estag.id = v.id\r\n               left join contratofp cfp on cfp.id = coalesce(aposent.contratofp_id, pen.contratofp_id, estag.id, v.id)\r\n               left join cargo on cfp.cargo_id = cargo.id\r\n               left join tiporegime tr on cfp.tiporegime_id = tr.id\r\n               left join modalidadecontratofp mcfp on cfp.modalidadecontratofp_id = mcfp.id\r\n               left join jornadadetrabalho jdt on jdt.id = cfp.jornadadetrabalho_id\r\n               left join atolegal ato on ato.id = cfp.atolegal_id\r\n      where trunc(folha.efetivadaem) between to_date(''01/01/'' || folha.ano, ''dd/MM/yyyy'') and to_date(''31/12/'' || folha.ano, ''dd/MM/yyyy'')\r\n        and trunc(folha.efetivadaem) between ho.iniciovigencia and coalesce(ho.fimvigencia, trunc(folha.efetivadaem)) $WHERE) dados\r\ngroup by servidor, cargo, vinculo, matricula, mes, ano, tipodefolha, dataadmissao, unidade, pensionista,\r\n         aposentado, estagiario, recurso, matricula, cpf, tipoRegime, jornadaTrabalho, ato, dataPublicacaoAto, idato, idpessoa","count":"select count(id) from fichafinanceirafp ff where ff.id is not null $WHERE","totalRegistros":23906,"fields":[{"posicao":1,"descricao":"Competência","columnName":"MESANO","columnValue":"MESANO","tipoAlinhamento":"ESQUERDA","totalizar":false,"link":null,"tipo":"STRING","tipoEnum":null,"valorPadrao":null,"escondido":false,"agrupador":false},{"posicao":2,"descricao":"Número de Identificação Funcional","columnName":"matricula","columnValue":"MATRICULA","tipoAlinhamento":"ESQUERDA","totalizar":false,"link":null,"tipo":"STRING","tipoEnum":null,"valorPadrao":null,"escondido":false,"agrupador":false},{"posicao":3,"descricao":"Nome Completo","columnName":"servidor","columnValue":"SERVIDOR","tipoAlinhamento":"ESQUERDA","totalizar":false,"link":{"chaveDestino":"pessoa-ver","columnOrigem":"IDPESSOA","columnDestino":"p.id"},"tipo":"STRING","tipoEnum":null,"valorPadrao":null,"escondido":false,"agrupador":false},{"posicao":4,"descricao":"CPF","columnName":"CPF","columnValue":"CPF","tipoAlinhamento":"ESQUERDA","totalizar":false,"link":null,"tipo":"STRING","tipoEnum":null,"valorPadrao":null,"escondido":false,"agrupador":false},{"posicao":5,"descricao":"Cargo","columnName":"cargo","columnValue":"CARGO","tipoAlinhamento":"ESQUERDA","totalizar":false,"link":null,"tipo":"STRING","tipoEnum":null,"valorPadrao":null,"escondido":false,"agrupador":false},{"posicao":6,"descricao":"Lotação","columnName":"unidade","columnValue":"unidade","tipoAlinhamento":"ESQUERDA","totalizar":false,"link":null,"tipo":"STRING","tipoEnum":null,"valorPadrao":null,"escondido":false,"agrupador":false},{"posicao":7,"descricao":"Regime jurídico","columnName":"tipoRegime","columnValue":"tipoRegime","tipoAlinhamento