  inner join licitacao lic on lic.id = ct.licitacao_id\r\n     ) dados\r\nwhere dados.tipoApuracao = ''ITEM'' $WHERE\r\norder by dados.rodada","count":"select count(1)\r\nfrom (select rod.numero                         as rodada,\r\n             lote.numero                        as lote,\r\n             ob.codigo || '' - '' || ob.descricao as Item,\r\n             itemfor.marca                      as marca,\r\n             p.id                               as participante,\r\n             rep.nome                           as responsavel,\r\n             itemfor.preco                      as valorPrevisto,\r\n             case\r\n                 when rod.numero = 1 then itemfor.preco\r\n                 else\r\n                     (select l.valor\r\n                      from lancepregao l\r\n                      where l.rodadapregao_id = (select max(rMax.id)\r\n                                                 from rodadapregao rMax\r\n                                                 where rMax.itempregao_id = item.id\r\n                                                   and l.propostafornecedor_id = lanc.propostafornecedor_id\r\n                                                   and rmax.numero < ((select max(rMax.numero)\r\n                                                                       from rodadapregao rMax\r\n                                                                       where rMax.id = lanc.rodadapregao_id)))\r\n                        and rownum = 1) end     as lanceAnterior,\r\n             lanc.valor                         as lanceAtual,\r\n             lanc.statuslancepregao             as situacao,\r\n             pregao.licitacao_id as idProcessoLicitatorio,\r\n             lic.tipoApuracao as tipoApuracao\r\n      from pregao\r\n               inner join itempregao item on item.pregao_id = pregao.id\r\n               inner join rodadapregao rod on rod.itempregao_id = item.id\r\n               inner join lancepregao lanc on lanc.rod