DECLARE
 texto CLOB := '{"consulta":"select conta.codigo || '' - '' || conta.descricao as contadereceita,\r\n       ho.descricao                             as unidade,\r\n       receita.operacaoreceita                  as operacao,\r\n       receita.valor                            as valor,\r\n       fonte.codigo || '' - '' || fonte.descricao as fontederecurso\r\nfrom receitaloa receita\r\n         inner join conta on receita.contadereceita_id = conta.id\r\n         inner join hierarquiaorganizacional ho on receita.entidade_id = ho.subordinada_id\r\n         inner join loa on receita.loa_id = loa.id\r\n         inner join ldo on loa.ldo_id = ldo.id\r\n         inner join exercicio e on ldo.exercicio_id = e.id\r\n         inner join receitaloafonte receitafonte on receita.id = receitafonte.receitaloa_id\r\n         inner join contadedestinacao cd on cd.id = receitafonte.destinacaoderecursos_id\r\n         inner join fontederecursos fonte on cd.fontederecursos_id = fonte.id\r\nwhere sysdate between ho.iniciovigencia and (coalesce(ho.fimvigencia, sysdate))\r\n  and ho.tipohierarquiaorganizacional = ''ORCAMENTARIA''\r\n  and e.ano = $ANO\r\n  $WHERE","count":"select count(1)\r\nfrom receitaloa receita\r\n         inner join conta on receita.contadereceita_id = conta.id\r\n         inner join hierarquiaorganizacional ho on receita.entidade_id = ho.subordinada_id\r\n         inner join loa on receita.loa_id = loa.id\r\n         inner join ldo on loa.ldo_id = ldo.id\r\n         inner join exercicio e on ldo.exercicio_id = e.id\r\n         inner join receitaloafonte receitafonte on receita.id = receitafonte.receitaloa_id\r\n         inner join contadedestinacao cd on cd.id = receitafonte.destinacaoderecursos_id\r\n         inner join fontederecursos fonte on cd.fontederecursos_id = fonte.id\r\nwhere sysdate between ho.iniciovigencia and (coalesce(ho.fimvigencia, sysdate))\r\n  and ho.tipohierarquiaorganizacional = ''ORCAMENTARIA''\r\n  and e.ano = $ANO\r\n  $WHERE","totalRegistros":235,"fields":[{"posicao":1,"descricao":"CONTA DE RECEITA","columnName":"CONTADERECEITA","columnValue":"CONTADERECEITA","tipoAlinhamento":"ESQUERDA","totalizar":false,"link":null,"tipo":"STRING","tipoEnum":null,"valorPadrao":null,"escondido":false},{"posicao":2,"descricao":"UNIDADE","columnName":"UNIDADE","columnValue":"UNIDADE","tipoAlinhamento":"ESQUERDA","totalizar":false,"link":null,"tipo":"STRING","tipoEnum":null,"valorPadrao":null,"escondido":false},{"posicao":3,"descricao":"OPERACAO","columnName":"OPERACAO","columnValue":"OPERACAO","tipoAlinhamento":"ESQUERDA","totalizar":false,"link":null,"tipo":"STRING","tipoEnum":null,"valorPadrao":null,"escondido":false},{"posicao":4,"descricao":"VALOR","columnName":"VALOR","columnValue":"VALOR","tipoAlinhamento":"CENTRO","totalizar":true,"link":null,"tipo":"MONETARIO","tipoEnum":null,"valorPadrao":null,"escondido":false},{"posicao":5,"descricao":"FONTE DE RECURSO","columnName":"FONTEDERECURSO","columnValue":"FONTEDERECURSO","tipoAlinhamento":"ESQUERDA","totalizar":false,"link":null,"tipo":"STRING","tipoEnum":null,"valorPadrao":null,"escondido":false}],"pesquisaveis":[{"descricao":"Unidade","columnName":"receita.entidade_id","columnValue":null,"tipo":"MULTI_SELECT","operacaoPadrao":null,"tipoEnum":null,"valorPadrao":"","tipoMultiSelect":"UNIDADE_ORCAMENTARIA"},{"descricao":"Fonte de Recurso","columnName":"fonte.codigo","columnValue":null,"tipo":"MULTI_SELECT","operacaoPadrao":null,"tipoEnum":null,"valorPadrao":"","tipoMultiSelect":"FONTE_DE_RECURSO"},{"descricao":"Natureza da Receita","columnName":"conta.codigo","columnValue":null,"tipo":"MULTI_SELECT","operacaoPadrao":null,"tipoEnum":null,"valorPadrao":"","tipoMultiSelect":"NATUREZA_DA_RECEITA"},{"descricao":"Operação da Receita","columnName":"receita.operacaoreceita ","columnValue":null,"tipo":"ENUM","operacaoPadrao":null,"tipoEnum":"br.com.webpublico.enums.OperacaoReceita","valorPadrao":[null,"RECEITA_DIRETAMENTE_ARRECADADA_BRUTA","RECEITA_CREDITOS_RECEBER_BRUTA","RECEITA_DIVIDA_ATIVA_BRUTA","DEDUCAO_RECEITA_RENUNCIA","DEDUCAO_RECEITA_RESTITUICAO","DEDUCAO_RECEITA_DESCONTO","DEDUCAO_RECEITA_FUNDEB","DEDUCAO_RECEITA_OUTRAS"],"tipoMultiSelect":null}],"tabs":[]}';
BEGIN
update PAGINAPREFEITURAPORTAL set CONTEUDO = texto where NOME = 'Planejamento da Receita' and CHAVE = 'planejamento-receita';
