insert into glossarioportal values (hibernate_sequence.nextval, 'ABERTURA DE CRÉDITO ADICIONAL', 'Decreto do Poder Executivo determinando a disponibilidade do crédito orçamentário, com base em autorização legislativa específica.');
insert into glossarioportal values (hibernate_sequence.nextval, 'AÇÃO', 'Atividade que o Poder Público precisa realizar para atender as necessidades da população.');
insert into glossarioportal values (hibernate_sequence.nextval, 'AÇÃO GOVERNAMENTAL', 'Conjunto de operações, cujos produtos contribuem para os objetivos do programa governamental.');
insert into glossarioportal values (hibernate_sequence.nextval, 'ACCOUNTABILITY', 'Obrigação que têm as pessoas, físicas ou jurídicas, públicas ou privadas, às quais se tenha confiado recursos públicos, de assumir as responsabilidades de ordem fiscal, gerencial e programática que lhes foram conferidas, e de informar à sociedade e a quem lhes delegou essas responsabilidades sobre o cumprimento de objetivos e metas e o desempenho alcançado na gestão dos recursos públicos. É, ainda, obrigação imposta a uma pessoa ou entidade auditada de demonstrar que administrou ou controlou os recursos que lhe foram confiados em conformidade com os termos segundo os quais eles lhe foram entregues.');
insert into glossarioportal values (hibernate_sequence.nextval, 'ADIMPLENTE', 'É aquele que cumpre com suas responsabilidades financeiras. Pagamento de possíveis tributos inscritos em dívida ativa, obtendo desde então, uma certidão negativa de débito.');
insert into glossarioportal values (hibernate_sequence.nextval, 'ADJUDICAÇÃO', 'Procuração dada a uma terceira parte, um agente fiduciário, que passa a ter amplos direitos de liquidar seus ativos para satisfazer as reivindicações de credores. No processo licitatório, é a manifestação oficial pela proposta mais vantajosa.');
insert into glossarioportal values (hibernate_sequence.nextval, 'ADMINISTRAÇÃO DIRETA', 'São as Secretarias e os Órgãos que compõem a estrutura administrativa do Poder Público, tais como: Secretaria de Educação, Secretaria de Saúde, Secretaria de Planejamento, etc.');
insert into glossarioportal values (hibernate_sequence.nextval, 'ADMINISTRAÇÃO FINANCEIRA', 'Ação de gerenciar as finanças públicas ou privadas.');
insert into glossarioportal values (hibernate_sequence.nextval, 'ADMINISTRAÇÃO INDIRETA', 'É uma estrutura ligada ao Poder Público, encarregada da execução de serviço específico. Ex: RBPREV, SAERB, RBTRANS, FGB e EMURB.');
insert into glossarioportal values (hibernate_sequence.nextval, 'ADMINISTRAÇÃO PÚBLICA', 'É o conjunto de órgãos e entidades, aos quais a Lei atribui a responsabilidade de atender aos interesses coletivos, tais como: Educação, Saúde, Transporte, dentre outras.');
insert into glossarioportal values (hibernate_sequence.nextval, 'ADMINISTRADOR PÚBLICO', 'Pessoa encarregada pela gestão de negócios públicos.');
insert into glossarioportal values (hibernate_sequence.nextval, 'AMORTIZAÇÃO DA DÍVIDA', 'Redução gradual de uma dívida por meio de pagamentos.');
insert into glossarioportal values (hibernate_sequence.nextval, 'ALIENAÇÃO', 'Alienar significa transferir, passar para outrem o domínio de coisa ou o gozo de determinado bem. A alienação de bens pela administração pública deve ser feita mediante licitação.');
insert into glossarioportal values (hibernate_sequence.nextval, 'ANO FINANCEIRO', 'Período de 1º de Janeiro a 31 de Dezembro.');
insert into glossarioportal values (hibernate_sequence.nextval, 'ANULAÇÃO DO EMPENHO', 'Cancelamento total ou parcial de importância empenhada.');
insert into glossarioportal values (hibernate_sequence.nextval, 'ARRECADAÇÃO', 'Estágio da Receita Pública subsequente ao lançamento. Consiste no recebimento da receita pelo agente devidamente autorizado e seu recolhimento aos cofres públicos.');
insert into glossarioportal values (hibernate_sequence.nextval, 'ATIVIDADE (ORÇAMENTO)', 'Conjunto de operações que se realizam de modo contínuo e que concorrem para a manutenção da ação do governo.');
insert into glossarioportal values (hibernate_sequence.nextval, 'ATIVIDADE ECONÔMICA', 'É uma atividade que gera rotatividade econômica, não se valendo, necessariamente, de lucros.');
insert into glossarioportal values (hibernate_sequence.nextval, 'ATIVO', 'Bens, direitos e valores pertencentes a uma empresa ou pessoa. Exemplo: imóveis, dinheiro aplicado, ações, jóias etc.');
insert into glossarioportal values (hibernate_sequence.nextval, 'ATIVO CIRCULANTE', 'Disponibilidades de numerário, recursos a receber, antecipações de despesa, bem como outros bens e direitos pendentes ou em circulação, realizáveis até o término do exercício seguinte.');
insert into glossarioportal values (hibernate_sequence.nextval, 'ATIVO COMPENSADO', 'Contas com função essencial de controle, relacionadas aos bens, direitos, obrigações e situações não compreendidas no patrimônio, mas que, direta ou indiretamente, possam vir a afetá-lo, inclusive os referentes a atos e fatos administrativos da execução orçamentária.');
insert into glossarioportal values (hibernate_sequence.nextval, 'ATIVO FINANCEIRO', 'Créditos e valores realizáveis independentemente de autorização orçamentária, bem como os valores numerários.');
insert into glossarioportal values (hibernate_sequence.nextval, 'ATIVO LÍQUIDO', 'Diferença positiva entre o ativo e o passivo.');
insert into glossarioportal values (hibernate_sequence.nextval, 'ATIVO PATRIMONIAL', 'Conjunto de valores e créditos que pertencem a uma entidade.');
insert into glossarioportal values (hibernate_sequence.nextval, 'ATIVO PERMANENTE', 'Bens, créditos e valores, cuja mobilização ou alienação dependa de autorização legislativa.');
insert into glossarioportal values (hibernate_sequence.nextval, 'AUTARQUIA', 'São entidades criadas pelo Poder Público, com autonomia própria, para desempenharem atividades públicas específicas. Ex: FGB, SAERB, RBTRANS, e RBPREV.');
insert into glossarioportal values (hibernate_sequence.nextval, 'AUXÍLIO', 'Ajuda concedida para fins diversos a outras esferas de governo ou entidades privadas sem fins lucrativos.');
insert into glossarioportal values (hibernate_sequence.nextval, 'BALANÇO', 'Demonstrativo que apresenta, em um dado momento, a situação financeira, patrimonial ou orçamentária de uma entidade pública.');
insert into glossarioportal values (hibernate_sequence.nextval, 'BALANÇO PATRIMONIAL', 'Demonstrativo contábil que apresenta, em um dado momento, a situação estática do patrimônio da entidade em termos de ativo, passivo e patrimônio líquido.');
insert into glossarioportal values (hibernate_sequence.nextval, 'BENEFICIÁRIO', 'São aqueles que recebem recursos públicos.');
insert into glossarioportal values (hibernate_sequence.nextval, 'BENEFÍCIOS FISCAIS', 'São considerados como reduções ou eliminações de ônus tributário em virtude de lei ou norma específica.');
insert into glossarioportal values (hibernate_sequence.nextval, 'CADASTRO DE FORNECEDORES', 'Cadastramento dos prestadores de serviços e/ou fornecedores de material ao serviço público.');
insert into glossarioportal values (hibernate_sequence.nextval, 'CARTÃO DE SUPRIMENTO DE FUNDOS', 'É um cartão de pagamentos para despesas com suprimentos de fundos.');
insert into glossarioportal values (hibernate_sequence.nextval, 'CARGO/EMPREGO', 'Cargo ou emprego que o servidor público ocupa dentro do Poder Público.');
insert into glossarioportal values (hibernate_sequence.nextval, 'CATEGORIA ECONÔMICA', 'Classificação detalhada da receita e despesa, com a finalidade de analisar a arrecadação e a despesa do Poder Público.');
insert into glossarioportal values (hibernate_sequence.nextval, 'CICLO ORÇAMENTÁRIO', 'Sequência de fases ou etapas que devem ser cumpridas como parte do processo orçamentário. A maioria dos autores adota como fases do ciclo orçamentário as seguintes: elaboração, apreciação legislativa, execução e acompanhamento, controle e avaliação, quando então se inicia o ciclo seguinte. Corresponde ao período de tempo em que se processam as atividades típicas do orçamento público, desde sua concepção até a apreciação final.');
insert into glossarioportal values (hibernate_sequence.nextval, 'COMPRA', 'Toda aquisição remunerada de bens e/ou serviços para fornecimento de uma só vez ou parceladamente.');
insert into glossarioportal values (hibernate_sequence.nextval, 'CONCEDENTE', 'Órgão financiador de um projeto que será executado através de um convênio com outro órgão.');
insert into glossarioportal values (hibernate_sequence.nextval, 'CONCORRÊNCIA', 'Modalidade de licitação para contratação de bens e serviços especiais e de obras e serviços comuns e especiais de engenharia.');
insert into glossarioportal values (hibernate_sequence.nextval, 'CONCURSO (LICITAÇÃO)', 'Modalidade de licitação para escolha de trabalho técnico, científico ou artístico, cujo critério de julgamento será o de melhor técnica ou conteúdo artístico, e para concessão de prêmio ou remuneração ao vencedor.');
insert into glossarioportal values (hibernate_sequence.nextval, 'CONCURSO PÚBLICO', 'É um processo seletivo que permite o acesso a emprego ou cargo público de modo amplo e democrático. É um procedimento impessoal onde é assegurada igualdade de oportunidades a todos interessados em concorrer para exercer as atribuições oferecidas pelo Estado, a quem incumbirá identificar e selecionar os mais adequados mediante critérios objetivos.');
insert into glossarioportal values (hibernate_sequence.nextval, 'CONTRAGARANTIA', 'Bem ou direito oferecido ao garantidor da dívida para ser utilizado no caso de inadimplência do devedor.');
insert into glossarioportal values (hibernate_sequence.nextval, 'CONTRAPARTIDA', 'Recursos de natureza financeira ou não, que uma entidade se compromete a aplicar na realização de um projeto.');
insert into glossarioportal values (hibernate_sequence.nextval, 'CONTRATADO', 'Aquele que assinou contrato com o governo para entrega de bens ou serviços.');
insert into glossarioportal values (hibernate_sequence.nextval, 'CONTRATANTE', 'Órgão ou entidade do governo que assinou contrato para aquisição de bens ou serviços.');
insert into glossarioportal values (hibernate_sequence.nextval, 'CONTRATO', 'Termo ou acordo, entre as partes, que define direitos e obrigações.');
insert into glossarioportal values (hibernate_sequence.nextval, 'CONVENENTE', 'Entidade que recebe recursos do Poder Público mediante convênio.');
insert into glossarioportal values (hibernate_sequence.nextval, 'CONVÊNIO', 'Instrumento realizado entre entidades, que possuem interesses comuns, que disciplina os repasses ou recebimentos de recursos públicos.');
insert into glossarioportal values (hibernate_sequence.nextval, 'CRÉDITO ADICIONAL', 'Autorização para a realização de despesas não previstas ou que foram insuficientemente previstas na lei do orçamento. Podem ser: Especial, Suplementar ou Extraordinário.');
insert into glossarioportal values (hibernate_sequence.nextval, 'CRÉDITO ESPECIAL', 'Autorização para a realização de despesas não previstas na lei do orçamento.');
insert into glossarioportal values (hibernate_sequence.nextval, 'CRÉDITO ORÇAMENTÁRIO', 'Valor previsto no orçamento para realização das ações de Poder Público.');
insert into glossarioportal values (hibernate_sequence.nextval, 'CRÉDITO SUPLEMENTAR', 'Autorização para a realização de despesas que foram insuficientemente previstas na lei do orçamento.');
insert into glossarioportal values (hibernate_sequence.nextval, 'CRÉDITO EXTRAORDINÁRIO', 'Disponibilização orçamentária para realização de despesas imprevisíveis e urgentes, tais como de calamidade pública.');
insert into glossarioportal values (hibernate_sequence.nextval, 'CREDOR', 'É quem ou a que se deve dinheiro ou qualquer outro valor (diz-se de pessoa física ou jurídica).');
insert into glossarioportal values (hibernate_sequence.nextval, 'CUSTOS', 'Gastos e despesas realizadas para o fornecimento de um produto ou serviço à sociedade.');
insert into glossarioportal values (hibernate_sequence.nextval, 'DADOS ABERTOS', 'Dados representados em meio digital em um formato sobre o qual nenhuma organização tenha controle exclusivo, passíveis de utilização por qualquer pessoa. Dados públicos representados em meio digital, estruturados em formato aberto, processáveis por máquina, referenciados na rede mundial de computadores e disponibilizados sob licença aberta que permita sua livre utilização, consumo ou cruzamento.');
insert into glossarioportal values (hibernate_sequence.nextval, 'DATA DA CELEBRAÇÃO', 'Data da assinatura do convênio.');
insert into glossarioportal values (hibernate_sequence.nextval, 'DATA DE EXERCÍCIO', 'Data de início das atividades do servidor público no cargo que ocupa.');
insert into glossarioportal values (hibernate_sequence.nextval, 'DATA DE VACÂNCIA', 'Data de término das atividades do servidor público no cargo que ocupa.');
