insert into glossarioportal values (hibernate_sequence.nextval, 'DATA DE FIM DE VIGÊNCIA', 'Data em que o convênio será encerrado.');
insert into glossarioportal values (hibernate_sequence.nextval, 'DATA DE REGISTRO DO CONVÊNIO', 'Data em que o convênio foi registrado pelo Poder Público.');
insert into glossarioportal values (hibernate_sequence.nextval, 'DÉFICIT', 'Excesso de despesa sobre a receita, quer na previsão, quer na realização.');
insert into glossarioportal values (hibernate_sequence.nextval, 'DÉFICIT FINANCEIRO', 'Maior saída de numerário em relação à entrada, em um determinado período.');
insert into glossarioportal values (hibernate_sequence.nextval, 'DÉFICIT ORÇAMENTÁRIO', 'Despesa maior do que receita, havendo distinção entre déficit previsto e o déficit da execução orçamentária.');
insert into glossarioportal values (hibernate_sequence.nextval, 'DÉFICIT PATRIMONIAL', 'Ativo menor do que o passivo.');
