insert into glossarioportal values (hibernate_sequence.nextval, 'DENÚNCIA', 'Comunicação de prática de ato ilícito cuja solução dependa da atuação de órgão de controle interno ou externo.');
insert into glossarioportal values (hibernate_sequence.nextval, 'DESPESA ANTECIPADA', 'É o pagamento efetuado de forma antecipada, de uma determinada despesa que ainda irá ocorrer.');
insert into glossarioportal values (hibernate_sequence.nextval, 'DESPESA COM PESSOAL E ENCARGOS SOCIAIS', 'São despesas com folha de pagamento dos servidores.');
insert into glossarioportal values (hibernate_sequence.nextval, 'DESPESA COM SERVIÇOS DE TERCEIRO', 'Gastos efetuados em contratação de serviços de terceiros.');
insert into glossarioportal values (hibernate_sequence.nextval, 'DESPESA CORRENTE', 'São despesas que não contribuem, diretamente, para formação ou aquisição de bens patrimoniais.');
insert into glossarioportal values (hibernate_sequence.nextval, 'DESPESA DE CAPITAL', 'São despesas que contribuem, diretamente, para formação ou aquisição de bens patrimoniais.');
insert into glossarioportal values (hibernate_sequence.nextval, 'DESPESA DE CUSTEIO', 'São gastos realizados pelo Poder Público para a manutenção de suas atividades básicas.');
insert into glossarioportal values (hibernate_sequence.nextval, 'DESPESA DE EXERCÍCIOS ANTERIORES', 'É o gasto realizado em anos anteriores, que por algum motivo, foi registrado somente no exercício atual.');
insert into glossarioportal values (hibernate_sequence.nextval, 'DESPESA EMPENHADA', 'É quando uma entidade separa um valor específico para o pagamento dos seus compromissos.');
