insert into glossarioportal values (hibernate_sequence.nextval, 'DESPESA LIQUIDADA', 'Despesa realizada, ou seja, o produto ou o serviço que foi entregue conforme contratado.');
insert into glossarioportal values (hibernate_sequence.nextval, 'DESPESA ORÇAMENTÁRIA', 'Despesas previstas no orçamento aprovado pela Câmara Municipal.');
insert into glossarioportal values (hibernate_sequence.nextval, 'DESPESA PÚBLICA', 'Gastos realizados pelo governo para funcionamento e manutenção dos serviços públicos prestados à sociedade.');
insert into glossarioportal values (hibernate_sequence.nextval, 'DIÁLOGO COMPETITIVO', 'É a modalidade de licitação, instituída pela Lei Federal nº 14.133/21, para contratação de obras, serviços e compras em que a Administração Pública realiza diálogos com licitantes previamente selecionados mediante critérios objetivos, com o intuito de desenvolver uma ou mais alternativas capazes de atender às suas necessidades, devendo os licitantes apresentar proposta final após o encerramento dos diálogos.');
insert into glossarioportal values (hibernate_sequence.nextval, 'DIÁRIAS – CIVIL', 'Gastos realizados para cobrir despesas com viagens do servidor civil (transporte, alimentação e pousada).');
insert into glossarioportal values (hibernate_sequence.nextval, 'DIÁRIAS – MILITAR', 'Gastos realizados para cobrir despesas com viagens do servidor militar (transporte, alimentação e pousada).');
insert into glossarioportal values (hibernate_sequence.nextval, 'DIÁRIAS NO PAÍS (DENTRO DO ESTADO)', 'É o valor para pagamento de despesas como alimentação, estadia e deslocamento que o servidor realizar em razão da viagem a trabalho dentro do Estado.');
insert into glossarioportal values (hibernate_sequence.nextval, 'DIÁRIAS NO PAÍS (FORA DO ESTADO)', 'É o valor para pagamento de despesas como alimentação, estadia e deslocamento que o servidor realizar em razão da viagem a trabalho fora do Estado.');
insert into glossarioportal values (hibernate_sequence.nextval, 'DIÁRIAS – SUPRIMENTO DE FUNDOS', 'Diárias cujo pagamento é realizado através de suprimento de fundos para atender situações excepcionais que fogem ao procedimento normal de pagamento.');
insert into glossarioportal values (hibernate_sequence.nextval, 'DIÁRIAS NO EXTERIOR', 'É o valor para pagamento de despesas como alimentação, estadia e deslocamento que o servidor realizar em razão da viagem a trabalho fora do Brasil.');
insert into glossarioportal values (hibernate_sequence.nextval, 'DISPENSA DE LICITAÇÃO', 'Modalidade de contratação direta, mediante licitação dispensada ou licitação dispensável.');
insert into glossarioportal values (hibernate_sequence.nextval, 'DISTRIBUIÇÃO DA RECEITA', 'É a entrega a outras esferas de governo de parte da receita arrecadada, conforme definido na constituição Federal ou por Lei Específica.');
insert into glossarioportal values (hibernate_sequence.nextval, 'DISTRIBUIÇÃO DE QUOTAS', 'Distribuição de parcelas mensais de recursos orçamentários aos órgãos e entidades do Poder Público.');
insert into glossarioportal values (hibernate_sequence.nextval, 'DÍVIDA ATIVA', 'É aquela dívida criada quando um devedor não paga seus débitos com o Poder Público, dentro do prazo estabelecido por lei.');
insert into glossarioportal values (hibernate_sequence.nextval, 'DÍVIDA CONSOLIDADA', 'Ver Dívida Fundada.');
insert into glossarioportal values (hibernate_sequence.nextval, 'DÍVIDA FINANCEIRA', 'Total das dívidas em contrato ou em títulos emitidos pelo governo.');
insert into glossarioportal values (hibernate_sequence.nextval, 'DÍVIDA FLUTUANTE PÚBLICA', 'Dívidas com vencimento inferior a doze meses.');
insert into glossarioportal values (hibernate_sequence.nextval, 'DÍVIDA FUNDADA PÚBLICA', 'Dívidas com vencimento superior a doze meses, contraídos para atender a um desequilíbrio orçamentário ou a um financiamento de obras e serviços públicos.');
insert into glossarioportal values (hibernate_sequence.nextval, 'DÍVIDA INTERNA PÚBLICA', 'Compromissos financeiros assumidos por entidade pública dentro do país, portanto, em moeda nacional.');
insert into glossarioportal values (hibernate_sequence.nextval, 'DÍVIDA PÚBLICA', 'Compromissos financeiros assumidos pelo Poder Público.');
insert into glossarioportal values (hibernate_sequence.nextval, 'DÍVIDA PÚBLICA EXTERNA', 'Compromissos assumidos por entidade pública no exterior, portanto, em moeda estrangeira.');
insert into glossarioportal values (hibernate_sequence.nextval, 'DÍVIDA NÃO CONSOLIDADA', 'Ver Dívida Flutuante Pública.');
insert into glossarioportal values (hibernate_sequence.nextval, 'DÍVIDA TOTAL', 'Total dos compromissos financeiros assumidos pelo governo.');
insert into glossarioportal values (hibernate_sequence.nextval, 'DOCUMENTO DE ARRECADAÇÃO FISCAL', 'Documento que se destina a arrecadar receitas tributárias federais.');
insert into glossarioportal values (hibernate_sequence.nextval, 'DOTAÇÃO', 'Valores destinados no orçamento, para atender determinada despesa.');
insert into glossarioportal values (hibernate_sequence.nextval, 'ELEMENTO DE DESPESA', 'Classificação detalhada da despesa que tem a finalidade de identificar os gastos do Poder Público.');
insert into glossarioportal values (hibernate_sequence.nextval, 'EMPENHO DA DESPESA', 'Reserva realizada pela autoridade competente para pagar certa conta, em face da contratação de serviços ou aquisição de produtos.');
insert into glossarioportal values (hibernate_sequence.nextval, 'EMPENHO GLOBAL', 'Reserva do valor total de um contrato, com pagamentos parcelados.');
insert into glossarioportal values (hibernate_sequence.nextval, 'EMPENHO ORDINÁRIO', 'Reserva do valor total de uma despesa para pagamento único.');
insert into glossarioportal values (hibernate_sequence.nextval, 'EMPENHO POR ESTIMATIVA', 'Reserva do valor estimado de um contrato a ser pago. Ex. água, Luz, telefone etc.');
insert into glossarioportal values (hibernate_sequence.nextval, 'EMPRESA PÚBLICA', 'Empresa de propriedade única do Poder Público, criada para realização de atividades desejadas pelo governo.');
insert into glossarioportal values (hibernate_sequence.nextval, 'ENTE DA FEDERAÇÃO', 'É a União, os Estados, o Distrito Federal e os Municípios.');
insert into glossarioportal values (hibernate_sequence.nextval, 'ENTIDADE SEM FINS LUCRATIVOS', 'É aquela entidade que não tem objetivo de lucros em suas operações.');
insert into glossarioportal values (hibernate_sequence.nextval, 'ERÁRIO', 'Recursos financeiros e econômicos do Estado.');
insert into glossarioportal values (hibernate_sequence.nextval, 'ESTÁGIOS DA DESPESA', 'Os estágios da despesa são: empenho, liquidação e pagamento. Empenho: é o ato emanado de autoridade competente que cria para o estado obrigação de pagamento, pendente ou não de implemento de condição; Liquidação: é a verificação do implemento de condição, ou seja, verificação objetiva do cumprimento contratual; Pagamento: é a emissão do cheque ou ordem bancária em favor do credor.');
insert into glossarioportal values (hibernate_sequence.nextval, 'ESTÁGIOS DA RECEITA', 'Os estágios da receita são: lançamento, arrecadação e recolhimento. Lançamento: é a relação individualizada dos contribuintes, discriminando a espécie, o valor e o vencimento do imposto de cada um; Arrecadação: é o momento em que os contribuintes comparecem perante aos agentes arrecadadores, a fim de liquidarem suas obrigações para com o estado; Recolhimento: é o ato pelo qual os agentes arrecadadores entregam diariamente ao Tesouro público o produto da arrecadação.');
insert into glossarioportal values (hibernate_sequence.nextval, 'EXECUÇÃO ORÇAMENTÁRIA', 'Atividade, realizada pelo Poder Público, de arrecadar receitas e realizar despesas de acordo com o previsto na Lei Orçamentária.');
insert into glossarioportal values (hibernate_sequence.nextval, 'EXERCÍCIO FINANCEIRO', 'Período correspondente à execução orçamentária. No Brasil, coincide com o ano civil (01 de Janeiro a 31 de Dezembro).');
insert into glossarioportal values (hibernate_sequence.nextval, 'EXERCÍCIO ANTERIOR', 'Referem-se às dívidas reconhecidas, resultantes de compromissos gerados em exercícios financeiros anteriores àquele em que deva ocorrer o pagamento, que, por motivo de força maior, não foram objeto de empenho.');
insert into glossarioportal values (hibernate_sequence.nextval, 'e-SIC', 'Neste canal, é possível fazer os pedidos de acesso à informação, via Lei n° 12.527/2011, de forma eletrônica para todos os órgãos da Prefeitura Municipal de Rio Branco.');
insert into glossarioportal values (hibernate_sequence.nextval, 'FALA.BR', 'Canal aonde você pode solicitar serviços e informações, além de fazer denúncias, reclamações, sugestões e elogios aos órgãos da Prefeitura Municipal de Rio Branco.');
insert into glossarioportal values (hibernate_sequence.nextval, 'FAVORECIDO', 'Quem recebeu o recurso pela prestação de serviço ou pela entrega do produto.');
insert into glossarioportal values (hibernate_sequence.nextval, 'FONTE DE RECURSOS', 'É a indicação detalhada de onde vem o dinheiro que está financiando cada item da Despesa realizada pelo governo.');
insert into glossarioportal values (hibernate_sequence.nextval, 'FUNÇÃO OU CARGO DE CONFIANÇA', 'Conjunto de atribuições inerentes ao exercício de funções especiais, chefia ou assessoramento.');
