insert into glossarioportal values (hibernate_sequence.nextval, 'PASSIVO', 'Contas relativas às obrigações, que uma pessoa física ou jurídica deve satisfazer. Evidencia as origens de recursos aplicados no ativo, dividindo-se em passivo circulante, exigível de curto e longo prazos, resultados de exercícios futuros, patrimônio líquido e passivo compensado.');
insert into glossarioportal values (hibernate_sequence.nextval, 'PASSIVO CIRCULANTE', 'Depósitos, restos a pagar, antecipações de receita, bem como outras obrigações pendentes ou em circulação, exigíveis até o término do exercício seguinte.');
insert into glossarioportal values (hibernate_sequence.nextval, 'PASSIVO COMPENSADO', 'Contas com função precípua de controle, relacionadas aos bens, direitos, obrigações e situações não compreendidas no patrimônio, mas que, direta ou indiretamente, possam vir a afetá-lo, inclusive as referentes a atos e fatos relacionados com a execução orçamentária e financeira.');
insert into glossarioportal values (hibernate_sequence.nextval, 'PATRIMÔNIO', 'Conjunto de bens direitos e obrigações de uma entidade.');
insert into glossarioportal values (hibernate_sequence.nextval, 'PATRIMÔNIO LÍQUIDO', 'Capital autorizado, as reservas de capital e outras que forem definidas, bem como o resultado acumulado e não destinado.');
