insert into glossarioportal values (hibernate_sequence.nextval, 'PESSOAL E ENCARGOS SOCIAIS', 'Despesa com o pagamento pelo efetivo serviço exercido de cargo/emprego ou função no setor público quer civil ou militar, ativo ou inativo, bem como as obrigações de responsabilidade do empregador.');
insert into glossarioportal values (hibernate_sequence.nextval, 'PLANEJAMENTO', 'Metodologia de administração que consiste, basicamente, em determinar os objetivos a alcançar, as ações a serem realizadas, compatibilizando-as com os meios disponíveis para sua execução.');
insert into glossarioportal values (hibernate_sequence.nextval, 'PLANO DE CONTRATAÇÃO ANUAL (PCA)', 'É o documento que consolida todas as compras e contratações que o órgão ou entidade pretende realizar ou prorrogar, no ano seguinte, e contempla bens, serviços, obras e soluções de tecnologia da informação.');
insert into glossarioportal values (hibernate_sequence.nextval, 'PLANO PLURIANUAL (PPA)', 'Lei elaborada pelo governo, que estabelece quais serão seus objetivos e metas para os próximos 04 anos.');
insert into glossarioportal values (hibernate_sequence.nextval, 'PRECATÓRIOS', 'São requisições de pagamento expedidas pelo Poder Judiciário para cobrar de municípios, estados ou da União, assim como de autarquias e fundações, valores devidos após condenação judicial definitiva.');
insert into glossarioportal values (hibernate_sequence.nextval, 'PREGÃO', 'Modalidade de licitação obrigatória para aquisição de bens e serviços comuns, cujo critério de julgamento poderá ser o de menor preço ou o de maior desconto.');
