insert into glossarioportal values (hibernate_sequence.nextval, 'PROGRAMA DE TRABALHO', 'Corresponde a ideias e propostas detalhadas que o Poder Público pretende executar.');
insert into glossarioportal values (hibernate_sequence.nextval, 'PROGRAMAÇÃO ORÇAMENTÁRIA', 'Instrumento que o Poder Público possui para organizar a utilização dos recursos que serão aplicados em suas ações.');
