insert into glossarioportal values (hibernate_sequence.nextval, 'RECEITA DE CONTRIBUIÇÕES', 'É o ingresso de recursos de contribuições sociais (como por exemplo, a Contribuição Patronal e do Servidor para o Regime de Previdência) dentre outras.');
