insert into glossarioportal values (hibernate_sequence.nextval, 'RESERVA DE CONTINGÊNCIA', 'Reserva de recursos orçamentários para eventos que poderão ocorrer no futuro.');
insert into glossarioportal values (hibernate_sequence.nextval, 'RESPONSABILIDADE FISCAL', 'É a execução por parte da administração governamental de políticas fiscais que busquem cumprir a meta de manter o equilíbrio nas contas públicas, reduzir o déficit primário ou aumentar o superávit primário, em conformidade com o que estabelece, no Brasil, a Lei Complementar n° 101 de 4 de Maio de 2000, a Lei de Responsabilidade Fiscal.');
insert into glossarioportal values (hibernate_sequence.nextval, 'RESUMO DO CONVÊNIO', 'Descrição resumida do objetivo do convênio.');
insert into glossarioportal values (hibernate_sequence.nextval, 'RESPONSÁVEL PELA PARTE DO CONCEDENTE', 'Nome da autoridade responsável pelo convênio dentro do Poder Público.');
insert into glossarioportal values (hibernate_sequence.nextval, 'RESPONSÁVEL PELA PARTE DO CONVENENTE', 'Nome da pessoa responsável pelo convênio na Prefeitura ou na Entidade beneficiária.');
