insert into glossarioportal values (hibernate_sequence.nextval, 'SERVIDOR ESTAGIÁRIO', 'Estagiário é o aluno matriculado e que esteja frequentando curso vinculado ao ensino em escola pública e /ou privada que desenvolve, no serviço público, atividades relacionadas à sua área de formação profissional.');
insert into glossarioportal values (hibernate_sequence.nextval, 'SERVIDOR REQUISITADO', 'É o servidor requisitado para trabalhar em local diferente do seu órgão de origem.');
