insert into glossarioportal values (hibernate_sequence.nextval, 'SOCIEDADE DE ECONOMIA MISTA', 'Empresa cuja maioria das ações com direito a voto pertencem ao Poder Público.');
insert into glossarioportal values (hibernate_sequence.nextval, 'SUBELEMENTO DE DESPESA', 'É uma subdivisão do elemento de despesa, indicando mais detalhadamente como a despesa se enquadra, ou seja, é uma subcategoria da despesa.');
insert into glossarioportal values (hibernate_sequence.nextval, 'SUBTÍTULO', 'Indica em qual localidade a despesa foi realizada.');
insert into glossarioportal values (hibernate_sequence.nextval, 'SUBSÍDIO', 'Concessão de dinheiro feita pelo Poder Público às empresas para lhes aumentar a renda, abaixar os preços ou para estimular as exportações do país.');
insert into glossarioportal values (hibernate_sequence.nextval, 'SUBVENÇÃO ECONÔMICA', 'Benefício concedido as empresas públicas para auxiliá-las a cobrir os prejuízos com sua manutenção.');
