insert into glossarioportal values (hibernate_sequence.nextval, 'TIPO DE OBJETO DE CONVÊNIO', 'Indica qual a classificação do objetivo do convênio, como obras, equipamentos, etc.');
insert into glossarioportal values (hibernate_sequence.nextval, 'TOTAL DAS REMUNERAÇÕES', 'Valor total pago ao grupo de servidores públicos ocupantes do mesmo cargo referente a salários, subsídios e outros.');
insert into glossarioportal values (hibernate_sequence.nextval, 'TOTAL - OUTRAS REMUNERAÇÕES', 'Valor total de remunerações extras (férias, auxilio alimentação, participação em comissões e conselhos, etc.) pagas aos servidores públicos ocupantes do mesmo cargo.');
insert into glossarioportal values (hibernate_sequence.nextval, 'TOMADA DE CONTAS', 'Ato pelo qual alguém recebe a prestação de contas, feita espontaneamente ou a pedido.');
insert into glossarioportal values (hibernate_sequence.nextval, 'TRANSFERÊNCIAS CONSTITUCIONAIS', 'São transferências (previstas na Constituição Federal) de parcelas das receitas arrecadadas pelo ente e que devem ser repassadas a outros entes da federação Exemplo: da União para Estados, Distrito Federal e Municípios ou do Estado para Municípios.');
insert into glossarioportal values (hibernate_sequence.nextval, 'TRANSFERÊNCIAS CORRENTES', 'Recursos recebidos ou destinados a terceiros sem a correspondente prestação de serviços, incluindo as subvenções sociais, os juros da dívida, a contribuição de previdência social.');
insert into glossarioportal values (hibernate_sequence.nextval, 'TRANSFERÊNCIAS DE CAPITAL', 'Quando uma entidade recebe ou repassa dinheiro à outra entidade para utilização em despesas de capital. Exemplo: recebimento de convênios da União pelo Estado e transferência de recurso do Estado para Municípios.');
insert into glossarioportal values (hibernate_sequence.nextval, 'TRANSFERÊNCIAS LEGAIS', 'Transferências (previstas em Leis Específicas) de parcelas das receitas arrecadadas pelo ente e que devem ser repassadas a outros entes da federação. Exemplo: da União para Estados, Distrito Federal e Municípios ou do Estado para Municípios.');
